package 字符;

public class 字符串比较函数 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String string1 = "hello ";
		String string2 = "HELLO ";
		System.out.println(string1.equals(string2));			//  equals  看两个字符串是否相等
		System.out.println(string1.equalsIgnoreCase(string2));	//  equalsIgnoreCase   忽略大小写的情况
	}

}
